import React from "react";
import axios from "axios"; 
import { useState,useEffect } from "react";



const Extend_dis = (i) =>{

  console.log(i.instance)
  return(

    <li>{i.instance.name.common}

          <button onClick={show(i.instance)}>
            show
          </button>
    </li>
  )
}


const show = ({props}) =>{
  console.log(props)
  const end_language = Object.values(props.languages)
  const end_capital = props.capital
  const picture =  props.flags.png
  const area = props.area 



  const list_languages = end_language.map(lan =><li> {lan}</li>)

  //  console
  return(
    <div>
      <h1>
            {props.name.common}

      </h1>

    <p>
        Capital {end_capital}
    </p>

    <p>
       Area {area}
    </p>

    <h2>
      languages
    </h2>

    <ul>
      {list_languages}
    </ul>

    <div>
      <img src={picture}/>
    </div>



    </div>
  )

}



const Display = (props) =>{
  if(props.content.length >=10 && props.search_content !== ""){
    const end_content =  "Too many matches, specify another filter"

    return(
      <div>
        <li>
          {end_content}
        </li>
      </div>
    )
  }
  else if (props.content.length === 1){
    const end_language = Object.values(props.content[0].languages)
    const end_capital = props.content[0].capital
    const picture =  props.content[0].flags.png
    const area = props.content[0].area 



    const list_languages = end_language.map(lan =><li> {lan}</li>)

    //  console
    return(
      <div>
        <h1>
              {props.content[0].name.common}

        </h1>

      <p>
          Capital {end_capital}
      </p>

      <p>
         Area {area}
      </p>

      <h2>
        languages
      </h2>

      <ul>
        {list_languages}
      </ul>

      <div>
        <img src={picture}/>
      </div>



      </div>
    )
  }
  else if(props.search_content === ""){
    return(
      <div>
        Please enter the country name!
      </div>
    )
  }


  else {
    console.log("------",props.content)

    return(

      <div>
        {props.content.map(cou => <Extend_dis instance = {cou}/> 

      )}
      </div>
    )
  }

}

const App = () =>{
  const [country, set_country] = useState([])
  const [new_country,set_new_country] = useState("")
  const [search_content,set_search_content] = useState("")


  const hook = () =>{
    console.log("test-11111")
    axios
    .get("https://restcountries.com/v3.1/all")
    .then( response =>{
      console.log("debug-22222")
      set_country(response.data)
    }

    )


  }

  useEffect(hook,[])

  const filter_content = (event) =>{
    console.log(event.target.value)
    set_search_content(event.target.value)
  }

  const content = country.filter(cou => cou.name.common.toLowerCase().includes(search_content.toLowerCase()) )
  //const end_content = (content.length<=10)? content : [{name:{common: "Too many matches, specify another filter"}}]

  return(

<div>

<form>
    <input onChange={filter_content}/>

    {/* {end_content.map(cou => <li>{cou.name.common}</li>)} */}

    <Display content ={content} search_content = {search_content}/>

</form>

    </div>
  )
}

export default App
