  return(
    <div>
    <p>
      {cou.course}
    </p>
    </div>
  )
}

const Content = (pa)=>{
  return(
    <div>
      <p>
        {pa.part1}{pa.ex1}
      </p>
      <p>
        {pa.part2}{pa.ex2}
      </p>
      <p>
        {pa.part2}{pa.ex2}
      </p>
    </div>
  )

}

const Total = (to)=>{
  return(
    <div>
      <p>
        Number of exercises {to.total}
      </p>
    </div>
  )
}

