import axios from "axios"

const base_URL = "http://localhost:3001/notes"

const get_all = () =>{
    return axios.get(base_url)
}


const create = new_object =>{
    return axios.post(base_URL,new_object)
}


const update = (id,new_object) =>{
    return axios.put(`${base_url}/${id}`,new_object)
}


export default {
    get_all: get_all,
    create: create,
    update: update
}
