//目前，添加到电话簿的号码没有被保存到后端服务器，修复这种情况！


import { useEffect, useState } from "react";
import Display from "./components/Display.js"
import axios from "axios";

const App = () =>{
  const [persons, setPersons] = useState ([])

  const [newName,setNewName] = useState("")

  const [new_number, set_new_number] =useState("")


  const [search_content, set_new_search] = useState("")


  const url = "http://localhost:3001/persons"


//先初始化！

const hook = () =>{
    axios.get(url).then
    (
        response => {

            setPersons(response.data)



        }

    )

}

useEffect(hook,[])


//下面添加賬戶内容！


const create  = ({new_object}) =>{
    axios.post(url,new_object).then(
        response =>
        {
            setPersons(persons.concat(response.data))
        }
    )
}











  const sub_eve = (event)=>{

    event.preventDefault()


    const  middle_array = persons.map(person => Object.values(person))
    const copy_name = newName

    for(let i = 0; i<persons.length;i++){
    if (middle_array[i][0] === copy_name){
      alert( newName + "  is already added to phonebook")
      setNewName(" ")
      set_new_number(" ")
      return 0
      }
    }

    const new_value = {
      name : newName,
      number : new_number
    }

    // setPersons(persons.concat(new_value))
    create(new_value)











    setNewName(" ")
    set_new_number(" ")

  }


  const search_everything  = (event) =>{

    set_new_search(event.target.value)

  }



  const content = persons.filter((person) => person.name.toLowerCase().includes(search_content.toLowerCase()))


  const change_holder = (event) =>{

    console.log("1111", event.target.value)

    setNewName(event.target.value)

  }

  const change_number = (event) =>{
    console.log(event.target.value)
    set_new_number(event.target.value)
  }

  return(
    <div>
      <h2>
        Phonebook
      </h2>


      <form onSubmit={sub_eve}>
        <div>
          name:    <input  onChange={change_holder}/>
        </div>

        <div>
          number:  <input onChange={change_number}/>
        </div>



        <div>
          <button type = "submit">
              add
          </button>
        </div>


      </form>



      <div>
          Search: <input onChange={search_everything}/>
      </div>


      <h2>
        Numbers
      </h2>

      <div>


      {content.map((person,id) => <Display key = {id} name = {person.name} number = {person.number}/>)}


      </div>
    </div>
  )

}
export default App
